import { Component, OnInit } from '@angular/core';
import { GoogleMapsLoader } from 'google-maps-js-api-loader';

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  async ngOnInit(){
    var loader=GoogleMapsLoader({
      key:"https://maps.googleapis.com/maps/api/js?key=AIzaSyAeqThAPBANYOIgu-7Rfw7rtvXCJk2ER4c&libraries=places"
    }).load()
  }

  selectedMenuItem = ''
  linkClick(menu : string)
  {
    this.selectedMenuItem = menu
  }
}
<div class="container">
    <div class="row mt-5">
        <div class="col-4">
            <button class="btn btn-primary" (click)="linkClick('auto')">Autocomplete</button>
        </div>
        <div class="col-8">
            <app-autocomplete *ngIf="selectedMenuItem == 'auto'"></app-autocomplete>
        
        </div>
    </div>
</div>
