import { Component } from '@angular/core';

@Component({
  selector: 'app-display-map',
  templateUrl: './display-map.component.html',
  styleUrls: ['./display-map.component.css']
})
export class DisplayMapComponent{

  map:any;
  coordinates:number[]=[-25.344,131.031];//this should come from config file 
  async displayMap(coordinates:number[]){

    if(coordinates.length!=0){
      this.coordinates=coordinates
    }
    this.map = new google.maps.Map(document.getElementById('map') as HTMLElement, {
      zoom: 14,
      center: { lat: this.coordinates[0], lng: this.coordinates[1] }
    });
    const marker = new google.maps.Marker({
      map: this.map,
      position: { lat: this.coordinates[0], lng: this.coordinates[1] },
      title: 'Uluru'
    });
    let delta_lat = (5 / 6371000) * (180 / Math.PI)
    let delta_lon = delta_lat / Math.cos(coordinates[0] * Math.PI / 180)
    let point1_lat = coordinates[0] + delta_lat
    let point1_lon = coordinates[1] + delta_lon

    let point2_lat = coordinates[0] - delta_lat
    let point2_lon = coordinates[1] + delta_lon

    let point3_lat = coordinates[0] - delta_lat
    let point3_lon = coordinates[1]- delta_lon

    let point4_lat = coordinates[0] + delta_lat
    let point4_lon = coordinates[1] - delta_lon

    console.log(point1_lat,point1_lon)

    const triangleCoords = [
      { lat:point1_lat, lng:point1_lon },
      { lat:point2_lat, lng:point2_lon },
      { lat:point3_lat, lng:point3_lon },
      { lat:point4_lat, lng:point4_lon },
    ];

    // Construct the polygon.
    const bermudaTriangle = new google.maps.Polygon({
      map:this.map,
      paths: triangleCoords,
      strokeColor: "#FF0000",
      strokeOpacity: 0.8,
      strokeWeight: 2,
      fillColor: "#FF0000",
      fillOpacity: 0.35,
    });


  }


}
