import { HtmlParser } from '@angular/compiler';
import { Component , ViewChild} from '@angular/core';
import { Loader } from '@googlemaps/js-api-loader';
@Component({
  selector: 'app-display-map',
  templateUrl: './display-map.component.html',
  styleUrls: ['./display-map.component.css']
})
export class DisplayMapComponent{
  map:any;
  coordinates:number[]=[-25.344,131.031];//this should come from config file 
  displayMap(coordinates:number[]){
    if(coordinates.length!=0){
      this.coordinates=coordinates
    }
    const mapOptions = {
      center: {
        lat: 0,
        lng: 0
      },
      zoom: 4
    };
    const loader = new Loader({
      apiKey: "https://maps.googleapis.com/maps/api/js?key=AIzaSyAeqThAPBANYOIgu-7Rfw7rtvXCJk2ER4c&libraries=places$callback=initMap",
      version: "weekly"
    });
    loader
  .load()
  .then((google) => {
    new google.maps.Map(document.getElementById("map") as HTMLElement, mapOptions);
  })
  .catch(e => {
    // do something
    console.log(e)
  });

    /*this.map = new google.maps.Map(document.getElementById('map') as HTMLElement, {
      zoom: 14,
      center: { lat: this.coordinates[0], lng: this.coordinates[1] }
    });*/
    const marker = new google.maps.Marker({
      map: this.map,
      position: { lat: this.coordinates[0], lng: this.coordinates[1] },
      title: 'Uluru'
    });


  }


}
